// Good - directives at beginning of line
  if (lopsidedScore)
  {
#if DISASTER_PENDING      // Correct -- Starts at beginning of line
      dropEverything();
# if NOTIFY               // OK but not required -- Spaces after #
      notifyClient();
# endif
#endif
      backToNormal();
  }

BODY.BAD_CODE_SNIPPET:
// Bad - indented directives
  if (lopsidedScore)
  {
      #if DISASTER_PENDING  // Wrong!  The "#if" should be at beginning of line
      dropEverything();
      #endif                // Wrong!  Do not indent "#endif"
      backToNormal();
  }

BODY.CODE_SNIPPET:
class MyClass : public OtherClass
{
public:
    MyClass();  // Regular 4 space / 1 tab indent.
    explicit MyClass(int var);
    ~MyClass() {}

    void someFunction();
    void someFunctionThatDoesNothing() {}

    void setSomeVar(int var) { _someVar = var; }
    int getSomeVar() const { return _someVar; }

private:
    bool someInternalFunction();

    int _someVar;
    int _someOtherVar;
    DISALLOW_COPY_AND_ASSIGN(MyClass);
};

// When it requires multiple lines put the colon on
// the first initializer line:
MyClass::MyClass(int var)
: some_var_(var),
  some_other_var_(var + 1) // lined up
{
    ...
    doSomething();
    ...
}

namespace {

void foo()  // Correct.  No extra indentation within namespace.
{
    ...
}

}  // namespace

BODY.BAD_CODE_SNIPPET:
namespace
{

    // Wrong.  Indented when it should not be.
    void foo()
    {
        ...
    }

}  // namespace

SUBSECTION.CODE_SNIPPET:
void f(bool b) {  // Open braces should always have a space before them.
  ...
int i = 0;  // Semicolons usually have no space before them.
int x[] = { 0 };  // Spaces inside braces for array initialization are
int x[] = {0};    // optional.  If you use them, put them on both sides!
// Spaces around the colon in inheritance and initializer lists.
class Foo : public Bar
{
public:
    // For inline function implementations, put spaces between the braces
    // and the implementation itself.
    Foo(int b) : Bar(), _baz(b) {}  // No spaces inside empty braces.
    void reset() { _baz = 0; }  // Spaces separating braces from implementation.
    ...

vector<string> x;           // No spaces inside the angle
y = static_cast<char*>(x);  // brackets (< and >), before
                            // <, or between >( in a cast.
vector<char *> x;           // Spaces between type and pointer are
                            // okay, but be consistent.
set<list<string> > x;       // C++ requires a space in > >.
set< list<string> > x;      // You may optionally use
                            // symmetric spacing in < <.
